import { NavLink } from "react-router-dom";

export default function Navbar() {
  const links = [
    { to: "/", label: "Home" },
    { to: "/departments/commercial", label: "Commercial" },
    { to: "/departments/logistic", label: "Logistic" },
    { to: "/departments/management", label: "Management" },
    { to: "/departments/client-service", label: "Client Service" },
  ];

  return (
    <nav className="bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 text-white shadow px-6 py-4 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <h1 className="text-2xl font-bold tracking-wide">Adex Portal</h1>
        <div className="flex gap-6 text-base font-medium">
          {links.map(({ to, label }) => (
            <NavLink
              key={to}
              to={to}
              className={({ isActive }) =>
                `relative px-2 py-1 group transition duration-300 ease-in-out
                 ${isActive ? "text-yellow-300" : "text-white"}`
              }
            >
              <span className="absolute scale-x-0 group-hover:scale-x-100 origin-left transition-transform duration-300 ease-in-out z-0 rounded"></span>
              <span className="relative z-10 group-hover:text-indigo-800 group-hover:font-semibold group-hover:scale-105 transition-all duration-300 ease-in-out">
                {label}
              </span>
            </NavLink>
          ))}
        </div>
      </div>
    </nav>
  );
}
