// src/pages/Home.jsx
import { Link } from "react-router-dom";
import { commercialAgents } from "../data/agents";
import AgentCard from "../components/AgentCard";

export default function Home() {
  const featuredAgents = commercialAgents.slice(0, 2); // Show 2 agents

  return (
    <div
      className="min-h-screen bg-cover bg-center text-white"
      style={{
        backgroundImage:
          "url('https://media.istockphoto.com/id/1218341272/photo/delivery-man-gives-postal-package-to-a-business-customer-who-signs-electronic-signature-pod.jpg?s=1024x1024&w=is&k=20&c=_xoor72hd7cPlKxNxVbdlzX-jLczAyG3tmw2HzigRg0=')",
      }}
    >
      <div className="bg-black bg-opacity-60 min-h-screen px-6 py-20">
        <div className="text-center mb-12" data-aos="fade-down">
          <h1 className="text-5xl font-bold mb-4">Welcome to Adex Portal</h1>
          <p className="text-lg max-w-2xl mx-auto">
            Discover all departments, meet our agents, and explore internal
            tools for Adex Company.
          </p>
        </div>

        {/* Department Cards */}
        <div
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 max-w-6xl mx-auto mb-16"
          data-aos="fade-up"
        >
          {[
            { name: "Commercial", path: "/departments/commercial" },
            { name: "Logistic", path: "/departments/logistic" },
            { name: "Management", path: "/departments/management" },
            { name: "Client Service", path: "/departments/client-service" },
          ].map((dept) => (
            <Link
              key={dept.name}
              to={dept.path}
              className="bg-white bg-opacity-90 text-indigo-800 font-semibold p-6 rounded-xl shadow-lg hover:scale-105 hover:bg-opacity-100 transition"
            >
              {dept.name} Department â†’
            </Link>
          ))}
        </div>

        {/* Featured Agents */}
        <div className="max-w-4xl mx-auto">
          <h2
            className="text-3xl font-bold text-center mb-6"
            data-aos="fade-right"
          >
            Meet Some of Our Agents
          </h2>
          <div
            className="grid grid-cols-1 sm:grid-cols-2 gap-6"
            data-aos="fade-up"
          >
            {featuredAgents.map((agent) => (
              <AgentCard key={agent.id} agent={agent} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
