import { Link } from "react-router-dom";

export default function Home() {
  return (
    <div className="min-h-screen bg-white px-6 py-12 text-center">
      <h1 className="text-4xl font-bold text-indigo-700 mb-4">
        Welcome to Adex Portal
      </h1>
      <p className="text-gray-600 mb-10 max-w-xl mx-auto">
        Your internal platform to explore all departments and contact our agents
        directly.
      </p>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 max-w-6xl mx-auto">
        {[
          { name: "Commercial", path: "/departments/commercial" },
          { name: "Logistic", path: "/departments/logistic" },
          { name: "Management", path: "/departments/management" },
          { name: "Client Service", path: "/departments/client-service" },
        ].map((dept) => (
          <Link
            to={dept.path}
            key={dept.name}
            className="p-6 bg-indigo-50 hover:bg-indigo-100 rounded-xl shadow text-indigo-700 font-semibold text-lg"
          >
            {dept.name} Department â†’
          </Link>
        ))}
      </div>
    </div>
  );
}
