// src/pages/Home.jsx
import { Link } from "react-router-dom";
import { commercialAgents } from "../data/agents";
import AgentCard from "../components/AgentCard";

import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

export default function Home() {
  const agents = commercialAgents;

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-50 to-indigo-100">
      <div className="text-center py-20" data-aos="fade-down">
        <h1 className="text-5xl font-bold text-indigo-700 mb-4">
          Welcome to Adex Portal
        </h1>
        <p className="text-gray-700 text-lg max-w-xl mx-auto">
          Your gateway to explore Adex departments and connect with our amazing
          agents.
        </p>
      </div>

      {/* SVG divider */}
      <svg
        className="-mt-10"
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 1440 100"
      >
        <path
          fill="#9333ea"
          fillOpacity="1"
          d="M0,64L40,58.7C80,53,160,43,240,58.7C320,75,400,117,480,128C560,139,640,117,720,96C800,75,880,53,960,74.7C1040,96,1120,160,1200,170.7C1280,181,1360,139,1400,117.3L1440,96L1440,0L1400,0C1360,0,1280,0,1200,0C1120,0,1040,0,960,0C880,0,800,0,720,0C640,0,560,0,480,0C400,0,320,0,240,0C160,0,80,0,40,0L0,0Z"
        ></path>
      </svg>

      {/* Carousel */}
      <div className="bg-purple-100 py-16">
        <h2
          className="text-center text-3xl font-bold text-indigo-800 mb-8"
          data-aos="fade-right"
        >
          Featured Agents
        </h2>

        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          spaceBetween={20}
          slidesPerView={1}
          breakpoints={{
            640: { slidesPerView: 1 },
            768: { slidesPerView: 2 },
            1024: { slidesPerView: 3 },
          }}
          navigation
          pagination={{ clickable: true }}
          autoplay={{ delay: 3000 }}
        >
          {agents.map((agent) => (
            <SwiperSlide key={agent.id}>
              <div className="max-w-xs mx-auto">
                <AgentCard agent={agent} />
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
}
