import { FaChartLine, FaEnvelope, FaWhatsapp } from "react-icons/fa";

// Dummy agents data example
const agents = [
  {
    id: 1,
    name: "Alice Smith",
    role: "Sales Manager",
    photo: "https://randomuser.me/api/portraits/women/44.jpg",
    email: "alice.smith@adex.com",
    whatsapp: "+1234567890",
  },
  {
    id: 2,
    name: "Bob Johnson",
    role: "Business Developer",
    photo: "https://randomuser.me/api/portraits/men/46.jpg",
    email: "bob.johnson@adex.com",
    whatsapp: "+1234567891",
  },
];

export default function DepartmentCommercial() {
  // ... AOS init and intro as before

  return (
    <div className="min-h-screen bg-gradient-to-b from-white to-purple-50 py-16 px-6">
      {/* Department intro as before */}

      {/* Agents Section */}
      <section className="max-w-6xl mx-auto mt-20">
        <h3 className="text-3xl font-semibold text-purple-700 mb-8 text-center">
          Meet Our Commercial Team
        </h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {agents.map(({ id, name, role, photo, email, whatsapp }) => (
            <div
              key={id}
              data-aos="fade-up"
              className="bg-white rounded-2xl shadow-lg p-6 flex flex-col items-center text-center hover:shadow-2xl transition duration-300"
            >
              <img
                src={photo}
                alt={name}
                className="w-28 h-28 rounded-full object-cover mb-4"
              />
              <h4 className="text-xl font-semibold text-purple-800">{name}</h4>
              <p className="text-purple-600 mb-4">{role}</p>
              <div className="flex space-x-4 text-purple-600">
                <a href={`mailto:${email}`} className="hover:text-purple-900">
                  <FaEnvelope size={20} />
                </a>
                <a
                  href={`https://wa.me/${whatsapp.replace(/\D/g, "")}`}
                  target="_blank"
                  rel="noreferrer"
                  className="hover:text-green-600"
                >
                  <FaWhatsapp size={20} />
                </a>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
