// src/components/AgentCard.jsx
import { Link } from "react-router-dom";
import { FaEnvelope, FaWhatsapp } from "react-icons/fa";

export default function AgentCard({ agent }) {
  return (
    <div
      className="bg-white p-5 rounded-xl shadow-md hover:shadow-xl transition duration-300 text-center"
      data-aos="fade-up"
    >
      <img
        src={agent.image}
        alt={agent.name}
        className="w-24 h-24 rounded-full mx-auto mb-4 border-2 border-indigo-500"
      />
      <h3 className="text-lg font-semibold text-indigo-700">{agent.name}</h3>
      <p className="text-sm text-gray-600">{agent.role}</p>

      <div className="flex justify-center gap-4 mt-3">
        <a
          href={`mailto:${agent.email}`}
          className="text-indigo-600 hover:text-indigo-800"
          title="Send Email"
        >
          <FaEnvelope size={18} />
        </a>
        {agent.whatsapp && (
          <a
            href={`https://wa.me/${agent.whatsapp}`}
            target="_blank"
            rel="noopener noreferrer"
            className="text-green-500 hover:text-green-600"
            title="Contact on WhatsApp"
          >
            <FaWhatsapp size={18} />
          </a>
        )}
        <Link
          to={`/agent/${agent.id}`}
          className="ml-2 text-sm text-indigo-500 hover:underline"
        >
          View profile â†’
        </Link>
      </div>
    </div>
  );
}
