// src/components/Navbar.jsx
import { NavLink } from "react-router-dom";

export default function Navbar() {
  const links = [
    { to: "/", label: "Home" },
    { to: "/departments/commercial", label: "Commercial" },
    { to: "/departments/logistic", label: "Logistic" },
    { to: "/departments/management", label: "Management" },
    { to: "/departments/client-service", label: "Client Service" },
  ];

  return (
    <nav className="bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 text-white shadow px-6 py-4 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <h1 className="text-2xl font-bold tracking-wide">Adex Portal</h1>

        <div className="flex gap-6 text-base font-medium">
          {links.map(({ to, label }) => (
            <NavLink
              key={to}
              to={to}
              className={({ isActive }) =>
                `relative px-3 py-1 group transition-all duration-300 ease-in-out 
                ${
                  isActive
                    ? "text-yellow-300 font-semibold"
                    : "text-white hover:text-yellow-300"
                }`
              }
            >
              {/* Hover background glow */}
              <span className="absolute inset-0 scale-0 group-hover:scale-100 bg-white opacity-10 rounded-full blur-sm transition-transform duration-500 ease-in-out z-0" />

              {/* Underline from center */}
              <span className="absolute left-1/2 -bottom-0.5 w-0 h-[2px] bg-yellow-300 group-hover:w-full group-hover:left-0 transition-all duration-300 ease-in-out z-10" />

              {/* Link label */}
              <span className="relative z-20 group-hover:scale-105 group-hover:tracking-wide transition-all duration-300 ease-in-out">
                {label}
              </span>
            </NavLink>
          ))}
        </div>
      </div>
    </nav>
  );
}
