// src/components/AgentCard.jsx
import { Link } from "react-router-dom";
import { FaEnvelope, FaWhatsapp } from "react-icons/fa";

export default function AgentCard({ agent }) {
  return (
    <div
      className="bg-white rounded-xl shadow-lg p-6 text-center hover:shadow-xl transition duration-300"
      data-aos="fade-up"
    >
      <img
        src={agent.image}
        alt={agent.name}
        className="w-24 h-24 rounded-full mx-auto border-4 border-indigo-200 mb-4"
      />
      <h3 className="text-xl font-bold text-indigo-700">{agent.name}</h3>
      <p className="text-gray-600 text-sm">{agent.role}</p>

      <div className="flex justify-center gap-4 mt-4 text-indigo-600">
        <a href={`mailto:${agent.email}`} title="Email">
          <FaEnvelope size={18} />
        </a>
        {agent.whatsapp && (
          <a
            href={`https://wa.me/${agent.whatsapp}`}
            target="_blank"
            rel="noopener noreferrer"
            title="WhatsApp"
            className="text-green-500"
          >
            <FaWhatsapp size={18} />
          </a>
        )}
      </div>

      <Link
        to={`/agent/${agent.id}`}
        className="mt-4 inline-block text-sm text-indigo-500 hover:underline"
      >
        View Profile â†’
      </Link>
    </div>
  );
}
